WITH RECURSIVE c0(src, trg) AS ((SELECT s0.src, s3.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0, edge s1, (SELECT trg as src, src as trg, label FROM edge) as s2, edge s3 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s2.trg = s3.src AND s0.label = AuthoredBy AND s1.label = AuthoredBy  AND s2.label = AuthoredBy  AND s3.label = AuthoredBy )) , c1(src, trg) AS ((SELECT s0.src, s3.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0, edge s1, (SELECT trg as src, src as trg, label FROM edge) as s2, edge s3 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s2.trg = s3.src AND s0.label = AuthoredBy AND s1.label = AuthoredBy  AND s2.label = AuthoredBy  AND s3.label = AuthoredBy  UNION SELECT s0.src, s3.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0, edge s1, (SELECT trg as src, src as trg, label FROM edge) as s2, edge s3 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s2.trg = s3.src AND s0.label = AuthoredBy AND s1.label = AuthoredBy  AND s2.label = AuthoredBy  AND s3.label = AuthoredBy  UNION SELECT s0.src, s3.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0, edge s1, (SELECT trg as src, src as trg, label FROM edge) as s2, edge s3 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s2.trg = s3.src AND s0.label = AuthoredBy AND s1.label = PublishedIn  AND s2.label = PublishedIn  AND s3.label = AuthoredBy )) , c2(src, trg) AS ((SELECT s0.src, s3.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0, edge s1, (SELECT trg as src, src as trg, label FROM edge) as s2, edge s3 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s2.trg = s3.src AND s0.label = AuthoredBy AND s1.label = PublishedIn  AND s2.label = PublishedIn  AND s3.label = AuthoredBy  UNION SELECT s0.src, s3.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0, edge s1, (SELECT trg as src, src as trg, label FROM edge) as s2, edge s3 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s2.trg = s3.src AND s0.label = AuthoredBy AND s1.label = AuthoredBy  AND s2.label = AuthoredBy  AND s3.label = AuthoredBy  UNION SELECT s0.src, s3.trg FROM (SELECT trg as src, src as trg, label FROM edge) as s0, edge s1, (SELECT trg as src, src as trg, label FROM edge) as s2, edge s3 WHERE s0.trg = s1.src AND s1.trg = s2.src AND s2.trg = s3.src AND s0.label = AuthoredBy AND s1.label = PublishedIn  AND s2.label = PublishedIn  AND s3.label = AuthoredBy )) SELECT DISTINCT c0.trg , c0.src, c1.trg , c2.trg  FROM c0, c1, c2 WHERE c0.src = c1.src AND c0.src = c2.src;
